mapper.update:
update reply
set content = #{content}
where rnum = #{rnum}

mapper.select:
 select rnum, rname, content, to_char(rdate, 'yyyy-mm-dd')rdate, ebook_ID, memberID, r
    from(
    select rnum, rname, content, rdate, ebook_ID, memberID, rownum r
    from(
        select rnum, rname, content, rdate, ebook_ID, memberID
        from ereview
            where ebook_ID = #{ebook_ID}
            order by rnum desc
        )  

)WHERE r >= #{sno} and r <= #{eno}
